import React, { Component, Fragment } from "react";

export default class SignUp extends Component {
 
constructor(props) {
    super(props);

    this.onChangeText = this.onChangeText.bind(this);
    this.saveText = this.saveText.bind(this);
    this.delitedText= this.delitedText.bind(this);
    this.state = {        
        save: false,       
        newTaskId : [] ,
        userOldData : [],
        userNewData : [],
        delitedText :false,
        textarea : ''

    }
}


onChangeText(e) {   
  this.setState({ 
    textarea: e.target.value    
 });
e.preventDefault();
}



saveText(e) { 
  
  this.setState({ newTaskId :  [{
    id: `f${(+new Date).toString(16)}`,
    mes: e.target.value    
}] });
  
if(this.state.userOldData.text.length != 0){  
  Array.prototype.push.apply(this.state.userOldData.text, this.state.newTaskId);      
  localStorage.setItem(this.props.user.email, JSON.stringify(this.state.userOldData));
  this.setState({save:!this.state.save}) 
  }
  
}
   
delitedText(text) {


  var newArray =[]; 

   for( var i = 0; i <  this.state.userOldData.text.length; i++){    
       if( this.state.userOldData.text[i].id != text.id){
        newArray.push(this.state.userOldData.text[i]); 
      //  this.state.userOldData.text.splice([i], 1)
      
      
        }           
    } 
    var oldData =   this.state.userOldData;
    oldData.text = newArray;
    var  newDelData = oldData;
    // Array.prototype.push(this.state.userOldData.text, delArr);

   localStorage.setItem(this.props.user.email, JSON.stringify(newDelData));
   this.state.userOldData = JSON.parse(localStorage.getItem(this.props.user.email));     
   this.setState({ userNewData :  this.state.userOldData });
   this.setState({delitedText:!this.state.delitedText}) ;
   

}


componentDidMount(){
    this.state.userOldData = JSON.parse(localStorage.getItem(this.props.user.email));     
    this.setState({ userNewData :  this.state.userOldData });
  }


 componentDidUpdate(prevProps, prevState) {

 


 }



    render() {
 
   
 
 
        return (

          <Fragment> 
{ this.state.userOldData.text && this.state.userOldData.text.map((text,index) => { 
    return (        
<div class="col-sm multi" key={index}>
<div  class="container mb-1"  >
<button type="button" class="btn btn-sm btn-outline-secondary">New</button></div>


<div class="row"  >
 
  <div class="col-md-4" >
    <div class="card mb-4 box-shadow">      
      <div class="card-body"  > 
        
        <form onSubmit={this.onSubmit}>
          <div class="row">
            <div class="col">
            <textarea class="form-control" id="textMes" aria-label="With textarea" rows="6" 
            wrap  defaultValue = {text.mes} 
              onChange={(e) => {this.onChangeText(e, "textMes") }}></textarea>
            </div> 
            
           
          </div>
        </form>
        
        <div class="d-flex justify-content-between align-items-center">
          <div class="btn-group mt-1">
            <button type="button" class="btn btn-sm btn-outline-secondary"  onClick={(e)=> {this.saveText(e)}}>Save</button>
            <button type="button" class="btn btn-sm btn-outline-secondary"  onClick={(e)=> {this.delitedText(text,e)}} >Delited</button>
          </div>
          {/* <small class="text-muted">{this.props.user.name}</small>
          <small class="text-muted">{this.props.user.email}</small> */}
          <small class="text-muted">{text.mes}</small>           
        </div>
        <small class="text-muted">{text.id}</small>         
      </div>
    </div>

  </div>

</div>


</div>)})}
</Fragment>


        );
    }
}
