import React, { Component } from "react";


export default class SignUp extends Component {

    userData;

    constructor(props) {
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            email: '',
            password: '',
            save: null
        }
    }

    // Form Values
    onChangeName(e) {
        this.setState({ name: e.target.value })

    }

    onChangeEmail(e) {
        this.setState({ email: e.target.value })

    }

    onChangePassword(e) {
        this.setState({ password: e.target.value })

    }


    // React Life Cycle
    componentDidMount() {

        this.userData = JSON.parse(localStorage.getItem(this.state.email));

        if (localStorage.getItem(this.state.email)) {
            this.setState({
                name: this.userData.name,
                email: this.userData.email,
                password: this.userData.password
            })
        } else {
            this.setState({
                name: '',
                email: '',
                password: ''
            })
        }
    }

    componentWillUpdate(nextProps, nextState) {       
        if(this.state.save == true){
        localStorage.setItem(this.state.email, JSON.stringify(nextState));
        this.setState({save:false})
        }
    }

    onSubmit(e) {       
        e.preventDefault()
        // console.log(this.state.props)
        this.setState({save:true})
        // let user = {[
        //     name: this.state.name,
        //     email: this.state.email,
        //     password: this.state.password
        // ]};

        // localStorage.setItem(this.state.email,user);

    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>First name</label>
                    <input type="text" className="form-control" placeholder="First name" 
                    value={this.state.name} onChange={this.onChangeName} />
                </div>           

                <div className="form-group">
                    <label>Email address</label>
                    <input type="text" className="form-control" placeholder="Enter email" 
                        value={this.state.email} onChange={this.onChangeEmail}
                    />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password"
                    defaultvalue={this.state.password} onChange={this.onChangePassword} />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                <p className="forgot-password text-right">
                    Already registered <a href="#">sign in?</a>
                </p>
            </form>
        );
    }
}
