import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom"


export default class TaskTable extends Component {

    

    constructor(props) {
        super(props);       
      

        this.state = {
            accounts : [],
            accountsAndTasksAndID :[]
        }
    }



    componentDidMount(){         
        this.state.accounts = JSON.parse(localStorage.getItem("accounts"));  
           
                
        this.state.accounts && this.state.accounts.map((account,index) => {
                    let user = JSON.parse(localStorage.getItem(account.email));
                    if(user && account.email == user.email ){
                        Array.prototype.push.apply(this.state.accountsAndTasksAndID, [{user}]);
                    }
                                      
                    
                })
               
                localStorage.setItem("usersData", JSON.stringify(this.state.accountsAndTasksAndID));
    }

 
    render() {
        

        var th = this.state.accountsAndTasksAndID && this.state.accountsAndTasksAndID.map((account,index) => {
            return (  
        <Fragment>
        <th scope="col">{index}</th>
        <th scope="col">{account.user.name}</th>
        </Fragment>
        )})


       
        return (
            <div class="container">
            <div class="row">
            <div class="col-md-4">
            
            <div class="col">task</div>
            <div class="col">task</div>
            <div class="col">task</div>
            <div class="col">task</div>
            <div class="col">task</div>
            <div class="col">task</div>


            </div>
              <div class="col-md-8">
              <div class="col">
            Виконавець
            <div class="row">
                  <div class="col-md-6">створив</div>
                  <div class="col-md-6">надіслав</div>
                </div>
                <div class="row">
                  <div class="col-md-6">завдання</div>
                  
                </div>
</div>
              </div>

              
              
            </div>
          </div>

        //   2 row
        
  
        );
    }
}
