import React, { Component } from "react";





export default class SignUp extends Component {
  userData;
constructor(props) {
    super(props);

    this.onChangeText = this.onChangeText.bind(this);
    this.saveText = this.saveText.bind(this);

    this.state = {        
        save: null,
        text: {}
    }
}


onChangeText(e) {
  var textTime = new Date();
  let text = {
    text: {textTime : e.target.value}
};
  this.setState({ text: text })
 
}



  saveText(e) {       
    e.preventDefault();
    this.setState({save:true}); 
  }
   
   
    

 componentDidUpdate(prevProps, prevState) {
  
  if(this.state.save !== prevState.save){
    let userData1 = JSON.parse(localStorage.getItem(this.props.user.email));



    let array4 = this.state.text;
    
    let array3 = Object.assign(userData1, array4);  


  localStorage.setItem(this.props.user.email, JSON.stringify(array3));
}
 }

