import React, { Component } from "react";




// let newTask;

export default class SignUp extends Component {
 
constructor(props) {
    super(props);

    this.onChangeText = this.onChangeText.bind(this);
    this.saveText = this.saveText.bind(this);
    this.newTextWindow = this.newTextWindow.bind(this);
    this.state = {        
        save: false,
        newTask : [],
        newTaskId : {text : [{}]},
        userOldData : [],
        userNewData : []

    }
}


onChangeText(e) { 
   this.setState({newTask : e.target.value});
}


saveText(e) {   
  this.setState({ newTaskId :{text :[{[`f${(+new Date).toString(16)}` ]: this.state.newTask}]   }});
  this.setState({save:!this.state.save});
  }
   
   

newTextWindow(){

  const masages =this.props.user.text.map((text)=>
  <div class="container">
<div  class="container mb-1">
<button type="button" class="btn btn-sm btn-outline-secondary">New</button></div>


<div class="row">
  <div class="col-md-4">
    <div class="card mb-4 box-shadow">      
      <div class="card-body"> 
        
        <form onSubmit={this.onSubmit}>
          <div class="row">
            <div class="col">
            <textarea class="form-control" readonly aria-label="With textarea" rows="6" 
            wrap defaultValue={text} onChange={this.onChangeText}></textarea>
            </div>  
          </div>
        </form>
        
        <div class="d-flex justify-content-between align-items-center">
          <div class="btn-group mt-1">
            <button type="button" class="btn btn-sm btn-outline-secondary" onClick = {this.saveText}>Save</button>
            <button type="button" class="btn btn-sm btn-outline-secondary">Edit</button>
          </div>
          <small class="text-muted">{this.props.user.name}</small>
          <small class="text-muted">{this.props.user.email}</small>          
        </div>
      </div>
    </div>

  </div>

</div>
</div>
  );

   return (
     <ul>
       {listItems}
     </ul>
   );



}

    

 componentDidUpdate(prevProps, prevState) {
  
  if(this.state.save !== prevState.save){
    this.state.userOldData = JSON.parse(localStorage.getItem(this.props.user.email)); 
    this.setState({ userNewData :  this.state.userOldData });  

  if(this.state.userOldData.text.length == 0){
    this.state.userNewData = Object.assign(this.state.userOldData,this.state.newTaskId);
    localStorage.setItem(this.props.user.email, JSON.stringify(this.state.userNewData));
   
  }else if(this.state.userOldData.text.length != 0){   
     this.state.userOldData.text.push(this.state.newTaskId.text); 
    localStorage.setItem(this.props.user.email, JSON.stringify(this.state.userOldData));     
  }
}
}



    render() {
    
     
    
        return (
         
          {newTextWindow}



        );
    }
}
