import React from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link, withRouter } from "react-router-dom";
import {createBrowserHistory} from 'history'
import Login from "./components/login.component";
import SignUp from "./components/signup.component";
import Task from "./components/task.component";
import { Redirect } from 'react-router'; 
const history = createBrowserHistory();


class App extends React.Component {
  constructor(props) {
    super(props);
    this.userLogin = this.userLogin.bind(this);
    this.state = { user : [], taskLogin : true };}



  userLogin(userlogined){
    this.setState({ user : userlogined, taskLogin : !this.state.taskLogin});


  }


  render() {
    // if (!this.state.taskLogin) {
    //   return <Redirect  push  to= {{ pathname: "/task"}}/>
    // }    
  return (<Router >
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container">
          <Link className="navbar-brand" to={"/sign-in"}></Link>
          <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-in"}>Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
              </li>

              <li className="nav-item">
                <Link className="nav-link" to={"/task"}>task</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {this.state.taskLogin && <div className="auth-wrapper">
        <div className="auth-inner">
          <Switch>
            <Route exact path='/' component={Login} />
            <Route history={history}  path="/sign-in" render={()=><Login userLogin = {this.userLogin}/>} />
            <Route history={history} path="/sign-up" render ={()=><SignUp />} />            
          </Switch>
        </div>
      </div> }

      {(!this.state.taskLogin && this.state.userlogined.email) &&<div className="auth-wrapper-task mt-5">
        <div className="auth-inner-task">
          <Switch>            
            <Route history={history}  path="/task" render ={()=><Task user = {this.state.user}/>} />
          </Switch>
        </div>
      </div>}
    </div>



    </Router>
  );
}
}

export default withRouter(App);
