import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom"


export default class TaskTable extends Component {

    

    constructor(props) {
        super(props);       
      

        this.state = {
            accounts : [],
            accountsAndTasksAndID : []
        }
    }



    componentDidMount(){         
        this.state.accounts = JSON.parse(localStorage.getItem("accounts"));   
        this.state.accountsAndTasksAndID = this.state.accounts.map((account,index) => {
            var user = JSON.parse(localStorage.getItem(account.email));
            Array.prototype.push.apply(this.state.accountsAndTasksAndID, user[index]); 

        })
         
               
      }
 
    render() {
        

        var th = this.state.accounts && this.state.accounts.map((account,index) => {
            return (  
                <Fragment>
            <th scope="col">{index}</th>
        <th scope="col">{account.account}</th>
        </Fragment>
        )})


        var td = this.state.accounts && this.state.accounts.map((account,index) => {
            return (  
                <Fragment>
                <th scope="row"></th>
       <td>{account.email}</td>  
       </Fragment>
        )})

        return (

        <table class="table">
        <thead>
           <tr>
           
           {th}
          
           
            </tr>
        </thead>
        <tbody>
        <tr>
       
        {td}
        
            
            </tr>      
        </tbody>
      </table>
  
        );
    }
}
