<div  class="container mb-1"  >
<button type="button" class="btn btn-sm btn-outline-secondary">New</button></div>


<div class="row"  >
 
  <div class="col-md-4" >
    <div class="card mb-4 box-shadow">      
      <div class="card-body"  > 
        
        <form onSubmit={this.onSubmit}>
          <div class="row">
            <div class="col">
            <textarea class="form-control" id="textMes" readonly aria-label="With textarea" rows="6" 
