          <div class="btn-group mt-1">
            <button type="button" class="btn btn-sm btn-outline-secondary"  onClick={(e)=> {this.saveText(text,index)}}>Save</button>
            <button type="button" class="btn btn-sm btn-outline-secondary"  onClick={(e)=> {this.delitedText(text,index)}} >Delited</button>
          </div>
          <small class="text-muted">{this.props.user.name}</small>
          <small class="text-muted">{this.props.user.email}</small>          
