import React, { Component } from "react";

export default class Login extends Component {
    userData;

    constructor(props) {
        super(props);


        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            email: '',
            password: '',
            welcome: false
        }
    }

    // Form Values


    onChangeEmail(e) {
        this.setState({ email: e.target.value })
    }

    onChangePassword(e) {
        this.setState({ password: e.target.value })
    }


    // React Life Cycle
    // componentDidMount() {
    //     this.setState({welcome : false});
    //     this.userData = JSON.parse(localStorage.getItem('user'));

    //     if (localStorage.getItem('user')) {


    //         this.setState({
    //             name: this.userData.name,
    //             email: this.userData.email,
    //             password: this.userData.password
    //         })
    //     } else {
    //         this.setState({
    //             name: '',
    //             email: '',
    //             password: ''
    //         })
    //     }
    // }

    // componentWillUpdate(nextProps, nextState) {




    //     this.state.email
    //     if (localStorage.getItem('user')) {
    //         this.setState({
    //             name: this.userData.name,
    //             email: this.userData.email,
    //             password: this.userData.password
    //         })
    // }

    onSubmit(e) {
        e.preventDefault()
        console.log(this.state.props)

        this.userData = JSON.parse(localStorage.getItem(this.state.email,this.state.email));
        if(this.state.email ===  this.userData.email && this.userData.password === this.state.password){
            this.setState({welcome : true});
        }
    }







    render() {
        return (
            <form onSubmit={this.onSubmit}>
                {!this.state.welcome && <h3> Sign In</h3>}

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email"
                     value={this.state.email} onChange={this.onChangeEmail} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password"
                    defaultvalue={this.state.password} onChange={this.onChangePassword} />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button   type="submit" className="btn btn-primary btn-block">Submit</button>

            </form>
        );
    }
}
