import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";


export default class TaskTable extends Component {

    

    constructor(props) {
        super(props);   

        this.state = {
            accounts : [],
            accountsAndTasksAndID :[]
        }
    }



    componentDidMount(){         
        this.state.accounts = JSON.parse(localStorage.getItem("accounts"));  
           
                
        this.state.accounts && this.state.accounts.map((account,index) => {
                    let user = JSON.parse(localStorage.getItem(account.email));
                    if(user && account.email == user.email ){
                        Array.prototype.push.apply(this.state.accountsAndTasksAndID, [{user}]);
                    }
                                      
                    
                })
               
                localStorage.setItem("usersData", JSON.stringify(this.state.accountsAndTasksAndID));
                this.forceUpdate();
    }

 
    render() {
        

       
        

       
        return (


            this.state.accountsAndTasksAndID && this.state.accountsAndTasksAndID.map((account,index) => {                 
                  var idAndMas = account.user.text.map((account)=>{   
               
                    return   <Fragment>
                        <div class="row">
                            <div class="col-md-6"><p>Creat: </p> {account.creator}</div>
                            <div class="col-md-6"><p>Get from: </p>{account.prevOwner}</div>
                        </div>
                        <div class="row">
                            <div class="col"><p><b>ID: </b>{account.id}</p> <p><b>TASK: </b>{account.mes}</p></div>
                        </div>
                     </Fragment>
           
                   

                })

                var Tasks = account.user.text.map((task)=>{ 

                    return <Fragment><li><p><b>ID: </b>{task.id}</p> <p><b>TASK: </b>{task.mes}</p> </li></Fragment>

                })

                return (  
           

                    <div class="container text-center TaskBord" key={index}>
                        <div class="row">
                            <div class="col-md-4 TaskBorder">
                                <div class="col ">
                                    <ol class="TaskBorder">
                                    
                                        {Tasks}
                                    </ol>
                                </div>
                            </div>
                            <div class="col-md-8">
                                <div class="row">
                                    <div class="col">
                                        <div class="row">
                                            <div class="col">  <p><b>ToDo: </b>{account.user.name} </p>
                                                <p><b>Mail: </b>{account.user.email}</p></div>
                                        </div>
                                        <div class="col">
                                            {idAndMas}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

       
        
        )})
        );
    }
}
