import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom"


export default class TaskTable extends Component {

    

    constructor(props) {
        super(props);       
      

        this.state = {
            accounts : [],
            accountsAndTasksAndID : []
        }
    }



    componentDidMount(){         
        this.state.accounts = JSON.parse(localStorage.getItem("accounts"));  
            if(this.state.accountsAndTasksAndID.length == 0){
                var obj;
                this.state.accounts.map((account,index) => {
                    let user = JSON.parse(localStorage.getItem(account.email));
                    if(obj == null){                   
                        obj = Object.assign(this.state.accountsAndTasksAndID, user);
                    }else{
                        Array.prototype.push.apply(obj, user);
                    }
               

                })
            }
    }
 
    render() {
        

        var th = this.state.accounts && this.state.accounts.map((account,index) => {
            return (  
                <Fragment>
            <th scope="col">{index}</th>
        <th scope="col">{account.account}</th>
        </Fragment>
        )})


        var td = this.state.accounts && this.state.accounts.map((account,index) => {
            return (  
                <Fragment>
                <th scope="row"></th>
       <td>{account.email}</td>  
       </Fragment>
        )})

        return (

        <table class="table">
        <thead>
           <tr>
           
           {th}
          
           
            </tr>
        </thead>
        <tbody>
        <tr>
       
        {td}
        
            
            </tr>      
        </tbody>
      </table>
  
        );
    }
}
