import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link, withRouter } from "react-router-dom";
import { Redirect } from 'react-router';



export default class Login extends Component {
    userData;

    constructor(props) {
        super(props);


        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state =
         {  email: '',
            password: '',
            userData: null,           
            welcome: null
        }
    }




    onChangeEmail(e) {
        this.setState({ email: e.target.value })
    }

    onChangePassword(e) {
        this.setState({ password: e.target.value })
    }




    onSubmit(e) {
        e.preventDefault()

        this.userData = JSON.parse(localStorage.getItem(this.state.email,this.state.email));

        if(!(this.userData == null)  && this.state.email ==  this.userData.email && this.userData.password === this.state.password){
            this.setState({welcome : true, userData : this.userData});            

        }
        this.stete.userData.splice(this.stete.welcome);
        this.props.userLogin( ); 
        }









    render() {
        if (this.state.welcome) {
            return <Redirect  push  to= {{ pathname: "/task", userData: { userData : this.state.userData}}}/>
          }


        return (
            <form onSubmit={this.onSubmit}>
                {this.state.welcome ? <h3> Sign In</h3> : <h3> wrong credentials</h3>}

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email"
                     value={this.state.email} onChange={this.onChangeEmail} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password"
                    defaultvalue={this.state.password} onChange={this.onChangePassword} />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button   type="submit" className="btn btn-primary btn-block">Submit</button> 





        {/* <Link  userData ={this.state.userData} to={"/task"}>
                   <button type="submit" className="btn btn-primary btn-block">
        Sign in </button>  </Link> */}
            </form>
        );
    }
}
